contracts/Pits.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import { Lib } from "./library/Lib.sol";
import { SafeTransferLib } from "solady/src/utils/SafeTransferLib.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {
    Initializable
} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {
    ReentrancyGuardUpgradeable
} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import { BalanceIsInsufficient } from "./library/Error.sol";
import { Ownable } from "solady/src/auth/Ownable.sol";

contract Pits is Initializable, Ownable, ReentrancyGuardUpgradeable {
    IERC20 public bones;
    uint256 public bonesStaked;

    uint256 public timeOut;

    uint256 public totalDaysOff;

    uint256 public minimumPercent;

    mapping(address => uint256) private balance;
    mapping(uint256 => uint256) private trackDaysOff;

    function initialize(address _bones) external initializer {
        _initializeOwner(msg.sender);
        bones = IERC20(_bones);
        minimumPercent = 3;
        __ReentrancyGuard_init();
    }

    function stakeBonesInYard(uint256 _amount) external nonReentrant {
        if (bones.balanceOf(msg.sender) < _amount)
            revert BalanceIsInsufficient();
        uint256 bonesBalance = bonesStaked;
        SafeTransferLib.safeTransferFrom(
            address(bones),
            msg.sender,
            address(this),
            _amount
        );
        balance[msg.sender] += _amount;
        bonesStaked += _amount;
        if (bonesBalance < minimumBonesRequired() && validation()) {
            uint256 daysOut;
            timeOut == 0 ? daysOut = 0 : daysOut =
                (block.timestamp - timeOut) /
                1 days;
            trackDaysOff[timeOut] = daysOut;
            totalDaysOff += daysOut;
        }

        emit StakeBonesInYard(msg.sender, _amount);
    }

    function removeBonesFromYard(uint256 _amount) external nonReentrant {
        if (_amount > balance[msg.sender]) revert BalanceIsInsufficient();
        uint256 bonesBalance = bonesStaked;
        balance[msg.sender] -= _amount;
        bonesStaked -= _amount;
        /**
         * The balance before was greater than the minimum
         * and now it is smaller than it
         */
        if (bonesBalance >= minimumBonesRequired() && !validation())
            timeOut = block.timestamp;
        SafeTransferLib.safeTransfer(address(bones), msg.sender, _amount);
        emit RemoveBonesFromYard(msg.sender, _amount);
    }

    function setMinimumPercent(uint256 _minimumPercent) external onlyOwner {
        minimumPercent = _minimumPercent;
    }

    function getTotalDaysOff() external view returns (uint256) {
        return totalDaysOff;
    }

    function getDaysOff(uint256 _timestamp) external view returns (uint256) {
        return trackDaysOff[_timestamp];
    }

    function minimumBonesRequired() internal view returns (uint256) {
        return (bones.totalSupply() * minimumPercent) / 10;
    }

    function getBonesStaked(address _addr) external view returns (uint256) {
        return balance[_addr];
    }

    function getTimeOut() external view returns (uint256) {
        return timeOut;
    }

    function getTotalBonesStaked() external view returns (uint256) {
        return bonesStaked;
    }

    function validation() public view returns (bool) {
        return bonesStaked >= minimumBonesRequired();
    }

    event StakeBonesInYard(address indexed owner, uint256 indexed amount);
    event RemoveBonesFromYard(address indexed owner, uint256 indexed amount);
}

contracts/Supplies.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import { Lib } from "./library/Lib.sol";
import { Ownable } from "solady/src/auth/Ownable.sol";
import { SafeTransferLib } from "solady/src/utils/SafeTransferLib.sol";
import { IBones } from "./interfaces/IBones.sol";
import {
    StringsUpgradeable
} from "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";
import { ITreasure } from "./interfaces/ITreasure.sol";
import {
    ERC1155Upgradeable
} from "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";

import {
    LengthsNotEqual,
    InvalidTokenId,
    NotAuthorized
} from "./library/Error.sol";

contract Supplies is ERC1155Upgradeable, Ownable {
    using StringsUpgradeable for uint256;

    address public laborGround;
    address public treasure;
    address public bones;
    address public magic;

    uint256 public constant MAGIC_PRICE = 10 ether;
    uint256 public constant BONES_PRICE = 1000 ether;
    uint256 public constant TREASURE_MOONROCK_VALUE = 5;

    // uint256 constant SHOVEL = 1;
    // uint256 constant SATCHEL = 2;
    // uint256 constant PICK_AXE = 3;

    string private baseUri;

    enum Curr {
        Magic,
        Bones,
        Treasure
    }

    function initialize(
        address _bones,
        address _magic,
        address _treasure,
        string memory _baseUri
    ) external initializer {
        _initializeOwner(msg.sender);
        bones = _bones;
        magic = _magic;
        treasure = _treasure;
        baseUri = _baseUri;
    }

    function setLaborGroundAddresss(address _laborGround) external onlyOwner {
        laborGround = _laborGround;
    }

    /**
     * this token can no be sold on the secondary market
     * only mint it and used for job
     */

    function mint(
        uint256[] calldata _tokenId,
        uint256[] calldata _amount,
        Curr[] calldata _curr
    ) public {
        uint256 i;
        if (_tokenId.length != _amount.length || _amount.length != _curr.length)
            revert LengthsNotEqual();
        for (; i < _tokenId.length; ++i) {
            if (_tokenId[i] > 3 || _tokenId[i] < 1) revert InvalidTokenId();
            payForToken(_curr[i], _amount[i]);
            _mint(msg.sender, _tokenId[i], _amount[i], "");
        }
    }

    function payForToken(Curr _curr, uint256 _amount) internal {
        if (_curr == Curr.Magic)
            SafeTransferLib.safeTransferFrom(
                magic,
                msg.sender,
                address(this),
                MAGIC_PRICE * _amount
            );
        if (_curr == Curr.Bones)
            IBones(bones).burn(msg.sender, BONES_PRICE * _amount);
        if (_curr == Curr.Treasure)
            ITreasure(treasure).burn(
                msg.sender,
                1,
                TREASURE_MOONROCK_VALUE * _amount
            );
    }

    function setApprovalForAll(
        address operator,
        bool approved
    ) public virtual override {
        if (operator != laborGround) revert NotAuthorized();
        super.setApprovalForAll(operator, approved);
    }

    function name() external pure returns (string memory) {
        return "Supplies";
    }

    function symbol() external pure returns (string memory) {
        return "supplies";
    }

    function withdraw() external onlyOwner {
        SafeTransferLib.safeTransferAll(magic, msg.sender);
    }

    function uri(
        uint256 _tokenId
    ) public view override returns (string memory) {
        return string(abi.encodePacked(baseUri, _tokenId.toString()));
    }
}

